    public Map<String, Map<Integer, FieldType>> getGroupWiseCubicFieldPositionAndTypeMap(String[] fieldPostions) {
      
      Map<String, Map<Integer, FieldType>> groupWiseCubicFieldPositionAndTypeMap = new HashMap<>();
      Map<String, List<String>> groupWiseCubicFieldMap = getAssemblyGroups().getGroupAndCubicFieldMap();
      Map<String, FieldType> fieldAndTypeMap = assemblyFields.getDimensions().getFieldAndTypeMap();
      for(String group : groupWiseCubicFieldMap.keySet()){
        Map<String, FieldType> cubicFieldAndTypeMap = new HashMap<>();
        List<String> cubicFields = groupWiseCubicFieldMap.get(group);
        for(String field : cubicFields){
          cubicFieldAndTypeMap.put(field, fieldAndTypeMap.get(field));
        }
        groupWiseCubicFieldPositionAndTypeMap.put(group, getFieldPositionAndTypeMap(fieldPostions, cubicFieldAndTypeMap));
      }
      return groupWiseCubicFieldPositionAndTypeMap;
    }
    

