    private Map<Integer, FieldType> getFieldPositionAndTypeMap(String[] fields, Map<String, FieldType> fieldAndTypeMap){
      Map<Integer, FieldType> fieldPositionAndTypeMap = new HashMap<>();
      for(int i=0;i<fields.length;i++){
        String field = fields[i];
        if(fieldAndTypeMap.containsKey(field)){
          fieldPositionAndTypeMap.put(i, fieldAndTypeMap.get(field));
        }
      }
      return fieldPositionAndTypeMap;
    }
